var fs=require("fs");
var ejs=require("ejs");



exports.jugement=function(file) {
    var dir=__dirname+file;
    var dirfile=[];
    var filedata="";
   fs.stat("e:/前端",function(err,stats){
       if(stats.isDirectory){
           fs.readdir("e:/前端",function(err,files){
            var i=0;
            dirfile=[];
            files.forEach(function(file){
                dirfile[i++]=file;
            });
           });
           return dirfile;
       }else if(stats.isFile){
           fs.readFile(dir,function(err,data){
                var filedata=data.toString();
                return data;
           })
       }else{
           return null;
       }
   });
   
}